mapper.select:
SELECT product_no, sum(stock) as stock, sum(sell_count) AS sales from `option`
product_no = ${product.productNo}
group by product_no

SELECT  option_no, color, size, stock
FROM   `option`
WHERE 	product_no = ${productNo}
		and color = #{color}
		and hidden_fl = 'N'

mapper.update:
update `option`
set		stock = ${stock},
        hidden_fl = #{hiddenFl}
where	option_no = ${optionNo}
