mapper.select:
SELECT `size` , color 
FROM `option`
WHERE product_no = #{productNo}
AND option_no = #{optionNo}

SELECT *
FROM `option` 
WHERE option_no = #{optionNo}
AND product_no = #{productNo}

SELECT product_name
FROM product
WHERE product_no = #{productNo}

mapper.update:
UPDATE `option`
SET stock = stock - ${count}, sell_count = sell_count + ${count}
WHERE product_no = ${productNo}
AND option_no = ${optionNo}

UPDATE product
SET product_state = 'S'
WHERE product_no = #{product_no}

UPDATE `member`
SET member_point = member_point + ${pointNoGain} - ${pointNoUse},
	member_totalpay = member_totalpay + ${orderPayment}
WHERE member_no = #{memberNo}

UPDATE coupon
SET coupon_fl = 'Y'
WHERE coupon_no = ${couponNo}

UPDATE order_product
SET order_product_delete_fl = 'Y'
WHERE order_no = #{orderNo}

UPDATE `option`
SET stock = stock + ${count}, sell_count = sell_count - ${count}
WHERE product_no = ${productNo}
AND option_no = ${optionNo}

UPDATE product
SET product_state = 'O'
WHERE product_no = #{productNo}

